<?phpif (! defined ( 'IN_VSF' )) {	print "<h1>Permission denied!</h1>You cannot access this area. (VS Framework is powered by <a href=\"http://www.vietsol.net\">Viet Solution webdesign company</a>)";	exit ();}global $vsStd;class home_public {	private $output = "";	private $html = "";		public function __construct(){		global $vsTemplate, $vsPrint;		$vsPrint->addCSSFile('article');		$this->html =  $vsTemplate->load_template('skin_home');	}		function auto_run() {		global $vsStd, $bw;		switch ($bw->input[1]){			default:				$this->loadDefault();		}	}		function loadDefault(){		global $vsStd, $vsTemplate, $vsMenu, $vsPrint;		$vsStd->requireFile(CORE_PATH.'pages/pages.php');		$pages = new pages();				$option['pageList'] = $pages->getAllObjByModule('about');				$cclass = array('even','odd');		foreach($option['pageList'] as $key => $page)			$option['pageList'][$key]->cclass = $cclass[($page->stt)%2];		$this->output = $this->html->loadDefault($option);	}		public function getHtml() {		return $this->html;	}		public function getOutput() {		return $this->output;	}		public function setHtml($html) {		$this->html = $html;	}	public function setOutput($output) {		$this->output = $output;	}}?>